import React from "react";
import { useState  } from "react";



const Popup = (props) => {
    const [firstName, setFirstName] = useState('')
    const [lastName, setLastName] = useState('')
    const [email, setEmail]=useState('')

    return ( props.trigger) ? (
        <div className="case-p">
            <div className="popup-inn">
           
            {/* CLOSE EBUTTON */}
            <button className="close" onClick={()=> props.setTrigger(false)}>&times;</button>

             <div className="register">
            <div className="inf">
                <h1>Register Now</h1>
                <p>You will get a firsthand access to the platform once it is launched.</p>
            </div>
            {/* Form section */}
            <form onSubmit={handleSubmit} className="entries">
                
                <input type="text" 
                 placeholder="First name" 
                 required
                value={firstName}
                onChange = {(e) => setFirstName(e.target.value)}
                />

                <input type="text"
                 placeholder="Last name" 
                 required
                 value={lastName}
                 onChange = {(e) => setLastName(e.target.value)}
                 />

                <input type="email" 
                 placeholder="Email Address"
                 required
                 value={email}
                 onChange = {(e) => setEmail(e.target.value)}

                 />
                <button className="reg-btn">Join</button>
                {/* <p>{firstName} {lastName} {email}</p> */}
            </form>
        </div>
            </div>
            <div className="Overlay"></div>
        </div>


      ): "";
}
 
export default Popup;
