import React from "react";
import mainlogo from "../images/connectskillz.svg"
import bann from "../images/Group 297.png"
import Popup from '../Components/popup';

import { useState, useEffect} from 'react';

const Header = () => {
    const [buttonPopup, setButtonPopup] = useState(false);
  
   
    // useEffect(()=>{
    //     setTimeout(()=>{
    //         setButtonPopup(true)    
    //     }, 3000);
    // }, [])
   
    return ( 
        <div className="box">
            <div className="navbar-cont">

                <div className="logi">
                    <img src={mainlogo} alt="" />
                    <div className="menu" >
                    <div className="bar"></div>
                    <div className="bar"></div>
                    <div className="bar"></div>
                </div>
                </div>

                <nav>
                    <ul>
                        <li><a href="#">About us</a></li>
                        <li><a href="#">FAQS</a></li>
                        <li><a href="#" className="spr-btn">Login</a></li>
                    </ul>


                </nav>

            </div>

            {/* <!-- First section --> */}

            <div className="container-vision">
                <div className="cont-vision">
                    <div className="cont-success">
                        <h2>Your success, our vision</h2>
                        <p>With our LMS we make your success attainable through a well structured learning pathway and
                            training from the best SME's in your desired field</p>

                      <button className="waiting" onClick={() => setButtonPopup(true)}  prop ={buttonPopup}>Join the Waiting list</button>
                    </div>

                    <div className="img-group">
                        <img src={bann} alt="" />
                    </div>
                </div>
            </div>
            <Popup trigger ={buttonPopup} setTrigger = {setButtonPopup} />
        </div>
        
     );
}
 
export default Header;
