import React from "react";
import { useRef, useState,useEffect } from "react";


// the popup function! it ultimate
const Popup = (props) => {
    const [firstName, setFirstName] = useState('')
    const [lastName, setLastName] = useState('')
    const [email, setEmail]=useState('')

    // submit form
    const handleSubmit = (e) => {
        e.preventDefault();
        const userData = {firstName, lastName, email};
        console.log(userData);
    }

    return ( props.trigger) ? (
        <div className="case-p">
            <div className="popup-inn">
           
            {/* CLOSE EBUTTON */}
            <button className="close" onClick={()=> props.setTrigger(false)}>&times;</button>

             <div className="register">
            <div className="inf">
                <h1>Register Now</h1>
                <p className="fic">You will get a firsthand access to the platform once it is launched.</p>
            </div>
            {/* Form section */}
            <form  className="entries">
        
                <input type="text" 
                 placeholder="First name" 
                 required
                 value={firstName}
                 onChange = {(e) => setFirstName(e.target.value)}
                />  

                <input type="text"
                 placeholder="Last name" 
                 required
                 value={lastName}
                 onChange = {(e) => setLastName(e.target.value)}
                 />

                <input type="email" 
                 placeholder="Email Address"
                 required
                 value={email}
                 onChange = {(e) => setEmail(e.target.value)}

                 />
                <button className="reg-btn">Join</button>
                <p>{firstName} {lastName} {email}</p>
            </form>
        </div>
            </div>
            <div className="Overlay"></div>
        </div>


      ): "";
}
 
export default Popup;
